mapper.select:
-- 변수목록조회 by 데이터셋... SELECT DB.DB_CONN_TRG_ID , DB.DB_CONN_TRG_PNM ,DB.DB_CONN_TRG_LNM ,S.DB_SCH_ID ,S.DB_SCH_PNM ,S.DB_SCH_LNM ,T.DBC_TBL_NM ,T.DBC_TBL_KOR_NM ,T.SUBJ_ID ,C.DBC_COL_NM ,C.DBC_COL_NM AS OBJ_NM ,C.DBC_COL_KOR_NM ,V.DATA_TYPE ,C.DATA_LEN ,C.DATA_PNT ,C.NULL_YN ,C.DEFLT_VAL ,C.PK_YN ,C.ORD ,C.PK_ORD , V.ANL_VAR_ID , V.VAR_TYPE
, DS.DASE_ID , OV.VAR_SNO , OV.OTL_ADD_CND , CASE WHEN OV.ANL_VAR_ID IS NOT NULL THEN '#0000FF' ELSE NULL END AS FontColor , OV.ANA_VAR_CHK AS ibsCheck , DP.DMNG_LNM AS DMN_PDT , IFNULL(DP.DMN_PRB, 0) * 100 AS DMN_PRB -- , '명칭' AS DMN_PDT -- , M.PRF_ID , V.OBJ_DESCN FROM WAA_DB_CONN_TRG DB INNER JOIN WAA_DB_SCH S ON DB.DB_CONN_TRG_ID = S.DB_CONN_TRG_ID AND S.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND S.REG_TYP_CD IN ('C','U') INNER JOIN WAT_DBC_TBL T ON DB.DB_CONN_TRG_ID = T.DB_CONN_TRG_ID AND S.DB_SCH_ID = T.DB_SCH_ID INNER JOIN WAT_DBC_COL C ON S.DB_SCH_ID = C.DB_SCH_ID AND T.DBC_TBL_NM = C.DBC_TBL_NM LEFT OUTER JOIN WAD_DATA_SET_NSTND DS ON T.DB_SCH_ID = DS.DB_SCH_ID AND T.DBC_TBL_NM = DS.DB_TBL_NM LEFT OUTER JOIN WAD_ANA_VAR_NSTND V ON V.DB_SCH_ID = C.DB_SCH_ID AND V.DB_TBL_NM = C.DBC_TBL_NM AND V.DB_COL_NM = C.DBC_COL_NM LEFT OUTER JOIN WAD_OTL_VAL OV ON V.ANL_VAR_ID = OV.ANL_VAR_ID AND OV.OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR} LEFT OUTER JOIN ( SELECT * FROM ( SELECT ANL_VAR_ID , DMNG_LNM , DMN_PRB , RANK() OVER(PARTITION BY ANL_VAR_ID ORDER BY DMN_PRB DESC ) AS RNK FROM WAD_DMN_PDT_NSTND ) WHERE RNK = 1 ) DP ON DP.ANL_VAR_ID = V.ANL_VAR_ID WHERE DB.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND DB.REG_TYP_CD IN ('C','U') AND S.DB_SCH_ID = #{dbSchId, jdbcType=VARCHAR} AND DS.DASE_ID = #{daseId, jdbcType=VARCHAR}
ORDER BY C.ORD

-- 변수목록 조회 by 데이터셋 목록 SELECT V.ANL_VAR_ID , CASE WHEN V.ANL_VAR_ID IS NOT NULL THEN 'U' ELSE 'I' END AS IBS_STATUS , DS.DASE_ID , S.DB_SCH_ID , S.DB_SCH_PNM , S.DB_SCH_LNM , T.DBC_TBL_NM AS DB_TBL_NM , C.DBC_COL_NM AS DB_COL_NM , TRIM(TRAILING C.DBC_COL_KOR_NM FROM '_0123456789') AS COL_MRP_LNM , TRIM(TRAILING C.DBC_COL_NM FROM '_0123456789') AS COL_MRP_PNM , DB.DBMS_TYP_CD , C.DATA_TYPE , IFNULL(C.DATA_LEN,0) AS DATA_LEN , IFNULL(C.DATA_PNT,0) AS DATA_SCAL , C.ORD , C.PK_YN FROM WAA_DB_CONN_TRG DB INNER JOIN WAA_DB_SCH S ON DB.DB_CONN_TRG_ID = S.DB_CONN_TRG_ID AND S.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND S.REG_TYP_CD IN ('C','U') INNER JOIN WAT_DBC_TBL T ON DB.DB_CONN_TRG_ID = T.DB_CONN_TRG_ID AND S.DB_SCH_ID = T.DB_SCH_ID INNER JOIN WAT_DBC_COL C ON S.DB_SCH_ID = C.DB_SCH_ID AND T.DBC_TBL_NM = C.DBC_TBL_NM JOIN WAD_DATA_SET_NSTND DS ON T.DB_SCH_ID = DS.DB_SCH_ID AND T.DBC_TBL_NM = DS.DB_TBL_NM LEFT OUTER JOIN WAD_ANA_VAR_NSTND V ON V.DB_SCH_ID = C.DB_SCH_ID AND V.DB_TBL_NM = C.DBC_TBL_NM AND V.DB_COL_NM = C.DBC_COL_NM WHERE DB.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND DB.REG_TYP_CD IN ('C','U') -- AND DS.DB_SCH_ID = '' AND DS.DASE_ID IN
#{item.daseId}
ORDER BY S.DB_SCH_ID, T.DBC_TBL_NM, C.ORD

mapper.update:
  -- 도메인판변 데이터타입 업데이트 by rqstNo
	UPDATE WAD_ANA_VAR X
	     SET DATA_TYPE = (
	         SELECT LGC_DATA_TYPE FROM (
	           
	           ) E WHERE 1=1
	                 AND X.DBMS_TYP_CD = E.DBMS_TYP_CD
		             AND UPPER(X.DATA_TYPE)   = UPPER(E.DATA_TYPE_NM)
		             AND IFNULL(X.DATA_LEN, -1) BETWEEN IFNULL(E.MIN_VAL,-1) AND IFNULL(E.MAX_VAL,-1) 
	     
	     )
	   WHERE DASE_ID IN (
	   	SELECT DASE_ID FROM WAD_DATA_SET WHERE RQST_NO = #{value} 
	   )

-- 도메인판별 최종결과 업데이트... UPDATE WAD_ANA_VAR_NSTND
DMNG_NM = #{dmngNm,jdbcType=VARCHAR}, WRIT_DTM = now(), WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR},
WHERE ANL_VAR_ID = #{anlVarId,jdbcType=VARCHAR}
