mapper.select:
select 

from WAD_OTL_ARG
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ALG_ARG_ID = #{algArgId,jdbcType=VARCHAR}

mapper.delete:
delete from WAD_OTL_ARG
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ALG_ARG_ID = #{algArgId,jdbcType=VARCHAR}

-- 이상값 탐지 알고리즘 파라미터 삭제 by 탐지ID
  delete from WAD_OTL_ARG
  where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}

mapper.update:
update WAD_OTL_ARG
set ARG_VAL = #{argVal,jdbcType=VARCHAR},
  OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
  OBJ_VERS = IFNULL(OBJ_VERS, 0) + 1,
  REG_TYP_CD = 'U',
  WRIT_DTM = now(),
  WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ALG_ARG_ID = #{algArgId,jdbcType=VARCHAR}
