mapper.select:
select 

from WAD_OTL_DATA
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm,jdbcType=TIMESTAMP}
  and OTL_SNO = #{otlSno,jdbcType=DECIMAL}

-- 컬럼정보 조회...
SELECT OTL_DTC_ID, ANA_STR_DTM, OTL_SNO, ANL_VAR_ID, COL_CNT, OTL_YN, OTL_RPL
  	 ${colnms}
  	
  FROM WAD_OTL_DATA A
  JOIN WAD_ANA_VAR
  WHERE OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
    AND ANL_VAR_ID = #{anlVarId,jdbcType=VARCHAR}
    AND OTL_SNO = 0

mapper.delete:
delete from WAD_OTL_DATA
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ANA_STR_DTM = #{anaStrDtm,jdbcType=TIMESTAMP}
  and OTL_SNO = #{otlSno,jdbcType=DECIMAL}

delete from WAD_OTL_DATA
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ANL_VAR_ID = #{anlVarId,jdbcType=VARCHAR}

mapper.insert:
insert into WAD_OTL_DATA (OTL_DTC_ID, ANA_STR_DTM, OTL_SNO, 
  ANL_VAR_ID, COL_CNT, COL_NM1
  )
values (#{otlDtcId,jdbcType=VARCHAR}, now(), #{otlSno,jdbcType=DECIMAL}, 
  #{anlVarId,jdbcType=VARCHAR}, #{colCnt,jdbcType=DECIMAL}, #{colNm1,jdbcType=VARCHAR} 
  )

INSERT INTO WAD_OTL_DATA (OTL_DTC_ID, ANA_STR_DTM, OTL_SNO, 
  ANL_VAR_ID, COL_CNT
  ${colnm}
  )
VALUES (#{otlDtcId,jdbcType=VARCHAR}, now(), #{otlSno,jdbcType=DECIMAL}, 
  #{anlVarId,jdbcType=VARCHAR}, #{colCnt,jdbcType=DECIMAL} 
  #{colval,jdbcType=VARCHAR}
  )

mapper.update:
  	UPDATE	WAD_OTL_DATA
    SET		OTL_YN		= #{otlYn, jdbcType=VARCHAR}
	    
	        , OTL_RPL = ''
	    
	    
	        , OTL_RPL = (SELECT MAX(OTL_RPL) 
	        				FROM WAD_OTL_DATA 
	        				WHERE OTL_DTC_ID = #{otlDtcId, jdbcType=VARCHAR}
	        					AND	ANL_VAR_ID	= #{anlVarId, jdbcType=VARCHAR} 
	        					AND OTL_SNO != 0)
	    
    WHERE	OTL_DTC_ID	= #{otlDtcId, jdbcType=VARCHAR}
    	AND	ANL_VAR_ID	= #{anlVarId, jdbcType=VARCHAR}
    	AND	OTL_SNO		= #{otlSno, jdbcType=VARCHAR}

	UPDATE	WAD_OTL_DATA
  SET		OTL_RPL		= #{otlRpl, jdbcType=VARCHAR}
  WHERE	OTL_DTC_ID	= #{otlDtcId, jdbcType=VARCHAR}
  	AND	ANL_VAR_ID	= #{anlVarId, jdbcType=VARCHAR}
  	AND	OTL_YN = 'Y'

mapper.delete:
	DELETE	FROM WAD_OTL_DATA_RPL
   WHERE	OTL_DTC_ID	= #{otlDtcId, jdbcType=VARCHAR}
     AND	ANL_VAR_ID	= #{anlVarId, jdbcType=VARCHAR}

mapper.insert:
  	INSERT INTO WAD_OTL_DATA_RPL
	    (${otlRplVal1})
	    (SELECT ${otlRplVal2}
	       FROM WAD_OTL_DATA A
	       		
	            ,(SELECT ${otlRplVal3}
	               FROM WAD_OTL_DATA
	              WHERE ANL_VAR_ID = #{anlVarId, jdbcType=VARCHAR}
	                AND OTL_DTC_ID = #{otlDtcId, jdbcType=VARCHAR}
	                AND OTL_SNO != 0
	                AND	OTL_YN = 'Y') B
	             
	      WHERE A.ANL_VAR_ID = #{anlVarId, jdbcType=VARCHAR}
	        AND A.OTL_DTC_ID = #{otlDtcId, jdbcType=VARCHAR}
	        AND A.OTL_SNO != 0
	        AND	OTL_YN = 'Y'
	        AND ANA_STR_DTM = (SELECT MAX(ANA_STR_DTM) FROM WAD_OTL_DATA WHERE ANL_VAR_ID = #{anlVarId, jdbcType=VARCHAR}
                                                                           AND OTL_DTC_ID = #{otlDtcId, jdbcType=VARCHAR})
	    )
