mapper.select:
select 

from WAD_OTL_VAL
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and VAR_SNO = #{varSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAD_OTL_VAL
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and VAR_SNO = #{varSno,jdbcType=DECIMAL}

--이상값 탐지 변수목록 삭제 by otlDtcId
  delete from WAD_OTL_VAL
  where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}

delete from WAD_OTL_VAL
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
  and ANL_VAR_ID = #{anlVarId,jdbcType=VARCHAR}

mapper.insert:
  
		SELECT IFNULL(MAX(VAR_SNO), 0) + 1 
		  FROM WAD_OTL_VAL 
		 WHERE OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR}
	
  insert into WAD_OTL_VAL 
  (  OTL_DTC_ID, VAR_SNO, ANL_VAR_ID 
   , OBJ_DESCN, OBJ_VERS, REG_TYP_CD 
   , WRIT_DTM, WRIT_USER_ID
   
   , ANA_VAR_CHK
   , COND_CD , OPR_CD , OPR_VAL
  )
  values 
  (   #{otlDtcId,jdbcType=VARCHAR}, #{varSno,jdbcType=DECIMAL}, #{anlVarId,jdbcType=VARCHAR} 
    , #{objDescn,jdbcType=VARCHAR}, 1, 'C', now(), #{writUserId,jdbcType=VARCHAR}
    
    , #{anaVarChk,jdbcType=VARCHAR}
    , #{condCd,jdbcType=VARCHAR}, #{oprCd,jdbcType=VARCHAR}, #{oprVal,jdbcType=VARCHAR}
  )

mapper.update:
update WAD_OTL_VAL
ANL_VAR_ID = #{anlVarId,jdbcType=VARCHAR}, OBJ_DESCN = #{objDescn,jdbcType=VARCHAR}, OBJ_VERS = IFNULL(OBJ_VERS, 0)+1, REG_TYP_CD = 'U', WRIT_DTM = now(), WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR},
where OTL_DTC_ID = #{otlDtcId,jdbcType=VARCHAR} and VAR_SNO = #{varSno,jdbcType=DECIMAL}

UPDATE WAD_OTL_VAL
   SET ANL_VAR_ID   = #{anlVarId,jdbcType=VARCHAR}
     , OBJ_DESCN    = #{objDescn,jdbcType=VARCHAR}
     , OBJ_VERS     = #{objVers,jdbcType=DECIMAL}
     , REG_TYP_CD   = #{regTypCd,jdbcType=VARCHAR}
     , WRIT_DTM     = #{writDtm,jdbcType=TIMESTAMP}
     , WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR}
     
     , ANA_VAR_CHK  = #{anaVarChk,jdbcType=VARCHAR}
     , COND_CD  	= #{condCd,jdbcType=VARCHAR}
     , OPR_CD  		= #{oprCd,jdbcType=VARCHAR}
     , OPR_VAL  	= #{oprVal,jdbcType=VARCHAR}
WHERE OTL_DTC_ID 	= #{otlDtcId,jdbcType=VARCHAR}
  AND VAR_SNO = #{varSno,jdbcType=DECIMAL}
