mapper.select:
SELECT 

FROM WAD_UODC_COL_DASE_DV
WHERE UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
  AND CRE_COMP_ID = #{creCompId,jdbcType=VARCHAR}

mapper.delete:
delete from WAD_UODC_COL_DASE_DV
where UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
  and CRE_COMP_ID = #{creCompId,jdbcType=VARCHAR}

mapper.insert:
insert into WAD_UODC_COL_DASE_DV (UDF_OTL_DTC_ID, CRE_COMP_ID, ANA_DASE_ID, 
  SRC_ANA_DASE_ID, OBJ_DESCN, OBJ_VERS, 
  REG_TYP_CD, WRIT_USER_ID, WRIT_DTM
  )
values (#{udfOtlDtcId,jdbcType=VARCHAR}, #{creCompId,jdbcType=VARCHAR}, #{anaDaseId,jdbcType=VARCHAR}, 
  #{srcAnaDaseId,jdbcType=VARCHAR}, #{objDescn,jdbcType=VARCHAR}, #{objVers,jdbcType=DECIMAL}, 
  #{regTypCd,jdbcType=VARCHAR}, #{writUserId,jdbcType=VARCHAR}, #{writDtm,jdbcType=TIMESTAMP}
  )

mapper.update:
update WAD_UODC_COL_DASE_DV
set ANA_DASE_ID = #{anaDaseId,jdbcType=VARCHAR},
  SRC_ANA_DASE_ID = #{srcAnaDaseId,jdbcType=VARCHAR},
  OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
  OBJ_VERS = #{objVers,jdbcType=DECIMAL},
  REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR},
  WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR},
  WRIT_DTM = #{writDtm,jdbcType=TIMESTAMP}
where UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
  and CRE_COMP_ID = #{creCompId,jdbcType=VARCHAR}

  	 
  	UPDATE WAD_UODC_COL_DASE_DV A
  	   SET ANA_DASE_ID = (SELECT ANA_DASE_ID
	                        FROM WAD_UODC_ANA_DASE X 
	                       WHERE X.UDF_OTL_DTC_ID = A.UDF_OTL_DTC_ID
	                         AND X.CRE_COMP_ID    = A.CRE_COMP_ID 
	                         AND ROWNUM <= 1
	                     )  
	 WHERE A.UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
	   AND A.CRE_COMP_ID    = #{creCompId,jdbcType=VARCHAR}
