mapper.select:
select 

from WAD_UODC_DASE_CND_DV_CND
where UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
  and CRE_COMP_ID = #{creCompId,jdbcType=VARCHAR}
  and CND_SNO = #{cndSno,jdbcType=DECIMAL}

mapper.delete:
delete from WAD_UODC_DASE_CND_DV_CND
where UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
  and CRE_COMP_ID = #{creCompId,jdbcType=VARCHAR}
  and CND_SNO = #{cndSno,jdbcType=DECIMAL}

DELETE FROM WAD_UODC_DASE_CND_DV_CND
 WHERE UDF_OTL_DTC_ID = #{udfOtlDtcId,jdbcType=VARCHAR}
   AND CRE_COMP_ID    = #{creCompId,jdbcType=VARCHAR}
