mapper.select:
select 

from WAD_UODC_ANA_DASE_COL
where ANA_DASE_ID = #{anaDaseId,jdbcType=VARCHAR}
  and ANA_COL_SNO = #{anaColSno,jdbcType=CHAR}

mapper.delete:
delete from WAD_UODC_ANA_DASE_COL
where ANA_DASE_ID = #{anaDaseId,jdbcType=VARCHAR}
  and ANA_COL_SNO = #{anaColSno,jdbcType=CHAR}

mapper.insert:
insert into WAD_UODC_ANA_DASE_COL (ANA_DASE_ID, ANA_COL_SNO, ANA_VAR_ID, 
  COL_PNM, OBJ_DESCN, OBJ_VERS, 
  REG_TYP_CD, WRIT_USER_ID, WRIT_DTM
  )
values (#{anaDaseId,jdbcType=VARCHAR}, #{anaColSno,jdbcType=CHAR}, #{anaVarId,jdbcType=VARCHAR}, 
  #{colPnm,jdbcType=VARCHAR}, #{objDescn,jdbcType=VARCHAR}, #{objVers,jdbcType=DECIMAL}, 
  #{regTypCd,jdbcType=VARCHAR}, #{writUserId,jdbcType=VARCHAR}, #{writDtm,jdbcType=TIMESTAMP}
  )

mapper.update:
update WAD_UODC_ANA_DASE_COL
set ANA_VAR_ID = #{anaVarId,jdbcType=VARCHAR},
  COL_PNM = #{colPnm,jdbcType=VARCHAR},
  OBJ_DESCN = #{objDescn,jdbcType=VARCHAR},
  OBJ_VERS = #{objVers,jdbcType=DECIMAL},
  REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR},
  WRIT_USER_ID = #{writUserId,jdbcType=VARCHAR},
  WRIT_DTM = #{writDtm,jdbcType=TIMESTAMP}
where ANA_DASE_ID = #{anaDaseId,jdbcType=VARCHAR}
  and ANA_COL_SNO = #{anaColSno,jdbcType=CHAR}

mapper.delete:
