mapper.select:
SELECT A.ABR_ID AS CODE_CD , CONCAT('(' , DATE_FORMAT(STR_DTM, '%Y/%m/%d') , ')' , CASE WHEN B.CNT > 0 THEN CONCAT(A.TERM_KNM , ' 외 ' , B.CNT , '건') ELSE A.TERM_KNM END ) AS CODE_LNM , '' AS CODE_PNM FROM WAM_STWD_ABR A INNER JOIN ( SELECT ABR_ID, COUNT(*) - 1 AS CNT FROM WAM_STWD_ABR GROUP BY ABR_ID ) B ON A.ABR_ID = B.ABR_ID WHERE A.DTL_SEQ = '1'
AND A.ORGL_USER = #{value}
ORDER BY A.STR_DTM DESC

SELECT A.CODE_CD , CONCAT( '(' , DATE_FORMAT(DV_RQST_DTM, '%Y/%m/%d') , ')' , CASE WHEN A.CNT > 0 THEN CONCAT(A.DV_ORG_LNM , ' 외 ' , A.CNT , '건') ELSE A.DV_ORG_LNM END) AS CODE_LNM , '' AS CODE_PNM FROM (SELECT DV_RQST_NO AS CODE_CD , MAX(DV_ORG_LNM) AS DV_ORG_LNM , MIN(DV_RQST_DTM) AS DV_RQST_DTM , COUNT(DISTINCT DV_ORG_LNM) - 1 AS CNT FROM ( select DV_RQST_NO , CONCAT( DIC_ASM_LNM , DMN_LNM ) as DIC_ASM_LNM ,SUBSTR( CONCAT(DIC_ASM_PNM , case when DMN_PNM is null then '' else '_' end, DMN_PNM),2) as DIC_ASM_PNM ,DMN_LNM ,DMN_PNM ,DV_ORG_LNM ,MIN(DV_RQST_DTM) AS DV_RQST_DTM FROM WAP_DV_CAN_ASM WHERE PRC_CD = 'Y' AND DV_SE_CD = 'I'
AND DV_RQST_USER_ID = #{value}
GROUP BY DV_RQST_NO, DIC_ASM_PNM, DIC_ASM_LNM, DMN_PNM, DMN_LNM, DV_ORG_LNM) A GROUP BY A.DV_RQST_NO) A ORDER BY CODE_CD DESC limit 20

SELECT A.CODE_CD , CONCAT( '(' , DATE_FORMAT(DV_RQST_DTM, '%Y/%m/%d') , ')' , CASE WHEN A.CNT > 0 THEN CONCAT(A.DV_ORG_LNM , ' 외 ' , A.CNT , '건') ELSE A.DV_ORG_LNM END) AS CODE_LNM , '' AS CODE_PNM FROM (SELECT DV_RQST_NO AS CODE_CD , MAX(DV_ORG_LNM) AS DV_ORG_LNM , MIN(DV_RQST_DTM) AS DV_RQST_DTM , COUNT(DISTINCT DV_ORG_LNM) - 1 AS CNT FROM ( select DV_RQST_NO , CONCAT( DIC_ASM_LNM , DMN_LNM ) as DIC_ASM_LNM ,SUBSTR( CONCAT(DIC_ASM_PNM , case when DMN_PNM is null then '' else '_' end, DMN_PNM),2) as DIC_ASM_PNM ,DMN_LNM ,DMN_PNM ,DV_ORG_LNM ,MIN(DV_RQST_DTM) AS DV_RQST_DTM FROM WAP_DB_DV_CAN_ASM WHERE PRC_CD = 'Y' AND DV_SE_CD = 'I'
AND DV_RQST_USER_ID = #{value}
GROUP BY DV_RQST_NO, DIC_ASM_PNM, DIC_ASM_LNM, DMN_PNM, DMN_LNM, DV_ORG_LNM) A GROUP BY A.DV_RQST_NO) A ORDER BY CODE_CD DESC limit 20

SELECT A.CODE_CD , CONCAT( '(' , DATE_FORMAT(DV_RQST_DTM, '%Y/%m/%d') , ')' , CASE WHEN A.CNT > 0 THEN CONCAT(A.DV_ORG_LNM , ' 외 ' , A.CNT , '건' ) ELSE A.DV_ORG_LNM END) AS CODE_LNM , '' AS CODE_PNM FROM (SELECT DV_RQST_NO AS CODE_CD , MAX(DV_ORG_LNM) AS DV_ORG_LNM , MIN(DV_RQST_DTM) AS DV_RQST_DTM , COUNT(DISTINCT DV_ORG_LNM) - 1 AS CNT FROM ( SELECT DV_RQST_NO , CONCAT(DIC_ASM_LNM , DMN_LNM) as DIC_ASM_LNM , SUBSTR( CONCAT( DIC_ASM_PNM , CASE WHEN DMN_PNM IS NULL THEN '' ELSE '_' END, DMN_PNM ),2) AS DIC_ASM_PNM , DMN_LNM , DMN_PNM , DV_ORG_LNM , MIN(DV_RQST_DTM) AS DV_RQST_DTM FROM WAP_DV_CAN_ASM WHERE PRC_CD = 'Y' AND DV_SE_CD = 'D'
AND DV_RQST_USER_ID = #{value}
GROUP BY DV_RQST_NO, DIC_ASM_PNM, DIC_ASM_LNM, DMN_PNM, DMN_LNM, DV_ORG_LNM) A GROUP BY A.DV_RQST_NO) A ORDER BY CODE_CD DESC
