mapper.select:
SELECT T.ORG_NM, --기관명 CASE WHEN T.COMM_ITEM_COUNT >0 THEN TO_CHAR(T.COMM_ITEM_COUNT) || ' / ' || TO_CHAR(ROUND(T.COMM_ITEM_COUNT / (SELECT COUNT(*) * 1.0 FROM COMM_SDITM) * 100.0,2))||'%' ELSE '0' END AS COMM_ITEM_COUNT_RATE, --공통표준 용어 T.ORG_ITEM_COUNT, --기관표준 용어 CASE WHEN T.COMM_DOMAIN_COUNT > 0 THEN TO_CHAR(T.COMM_DOMAIN_COUNT) || ' / ' || TO_CHAR(ROUND(T.COMM_DOMAIN_COUNT / (SELECT COUNT(*) * 1.0 FROM COMM_DMN) * 100.0,2))||'%' ELSE '0' END AS COMM_DOMAIN_COUNT_RATE, --공통표준 도메인 T.ORG_DOMAIN_COUNT, --기관표준 도메인 CASE WHEN T.COMM_WORD_COUNT > 0 THEN TO_CHAR(T.COMM_WORD_COUNT) || ' / ' || TO_CHAR(ROUND(T.COMM_WORD_COUNT / (SELECT COUNT(*) * 1.0 FROM COMM_STWD) * 100.0,2))||'%' ELSE '0' END AS COMM_WORD_COUNT_RATE, --공통표준 단어 T.ORG_WORD_COUNT --기관표준 단어 FROM (SELECT WO.ORG_NM, (SELECT COUNT(*) FROM WAM_SDITM WHERE ORG_NM=WO.ORG_NM) AS ORG_ITEM_COUNT, (SELECT COUNT(*) FROM WAM_DMN WHERE ORG_NM=WO.ORG_NM) AS ORG_DOMAIN_COUNT, (SELECT COUNT(*) FROM WAM_STWD WHERE ORG_NM=WO.ORG_NM) AS ORG_WORD_COUNT, (SELECT COUNT(*) FROM COMM_SDITM A INNER JOIN (SELECT SDITM_LNM,SDITM_PNM,INFOTP_LNM FROM WAM_SDITM WHERE ORG_NM = WO.ORG_NM) B ON (A.SDITM_LNM =B.SDITM_LNM AND A.SDITM_PNM = B.SDITM_PNM AND A.INFOTP_LNM =B.INFOTP_LNM) ) AS COMM_ITEM_COUNT, (SELECT COUNT(*) FROM COMM_DMN A INNER JOIN (SELECT INFOTP_LNM,DATA_LEN,DATA_TYPE FROM WAM_DMN WHERE ORG_NM = WO.ORG_NM) B ON(A.INFOTP_LNM=B.INFOTP_LNM AND A.DATA_LEN = B.DATA_LEN AND A.DATA_TYPE = B.DATA_TYPE) ) AS COMM_DOMAIN_COUNT, (SELECT COUNT(*) FROM COMM_STWD A INNER JOIN (SELECT STWD_LNM,STWD_PNM FROM WAM_STWD WHERE ORG_NM = WO.ORG_NM) B ON(A.STWD_LNM=B.STWD_LNM AND A.STWD_PNM = B.STWD_PNM) ) AS COMM_WORD_COUNT FROM WAA_ORG WO
WHERE WO.ORG_NM LIKE '%' || #{orgNm} || '%'
) T
