mapper.update:
UPDATE WAA_USER
SET REG_TYP_CD = #{regTypCd}
where USER_ID = #{userId,jdbcType=VARCHAR}

mapper.select:
SELECT
A.USER_ID, A.EXP_DTM, A.STR_DTM, A.LOGIN_AC_ID, A.LOGIN_AC_PWD, A.USER_NM, A.USERG_ID, A.DEPT_ID, A.JGD_NM, A.USER_TELNO, A.USER_HTELNO, A.EMAIL_ADDR, A.EXCL_DWLD_AUTH_YN, A.ID_USE_EXP_DT, A.PWD_EXP_DT, A.OBJ_DESCN, A.OBJ_VERS, A.REG_TYP_CD, A.RQST_DTM, A.RQST_USER_ID, A.APRV_DCD, A.APRV_DTM, A.APRV_USER_ID, C.DEPT_NM, C.DEPT_PATH , CASE WHEN C.DEPT_ID = (SELECT A.DEPT_ID FROM WAA_USER A WHERE A.USER_ID = #{userId} AND A.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') AND A.REG_TYP_CD IN ('C', 'U')) THEN 0 ELSE NULL END AS DEPT_SEQ FROM WAA_USER A
LEFT OUTER JOIN ( SELECT A.DEPT_ID , B.DEPT_ID AS UPP_DEPT_ID , A.DEPT_NM , A.DEPT_LVL , A.ALL_DEPT_NM AS DEPT_PATH FROM (
) A LEFT OUTER JOIN WAA_DEPT B ON A.UPP_DEPT_ID = B.DEPT_ID AND B.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') AND B.REG_TYP_CD IN ('C','U')
AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d')
) C ON A.DEPT_ID = C.DEPT_ID where 1=1 AND A.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') AND A.REG_TYP_CD IN ('C', 'U')
AND A.USER_NM LIKE CONCAT('%' , #{userNm,jdbcType=VARCHAR} , '%')
AND A.USERG_ID = #{usergId,jdbcType=VARCHAR}
AND C.DEPT_NM LIKE CONCAT('%' , #{deptNm,jdbcType=VARCHAR} , '%')
AND A.DEPT_ID = #{deptId,jdbcType=VARCHAR}
AND A.USER_TELNO = #{userTelno,jdbcType=VARCHAR}
AND A.USER_HTELNO = #{userHtelno,jdbcType=VARCHAR}
AND A.USER_ID IN (select Y.USER_ID from WAA_APRG X INNER JOIN WAA_APRG_USER Y ON X.APRG_ID =Y.APRG_ID WHERE X.APRG_ID = 'OBJ_00000007348' AND X.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND X.REG_TYP_CD IN ('C', 'U') AND Y.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND Y.REG_TYP_CD IN ('C', 'U'))
ORDER BY DEPT_SEQ, C.DEPT_PATH, A.USER_NM

SELECT A.USER_ID, A.EXP_DTM, A.STR_DTM, A.LOGIN_AC_ID, A.USER_NM, A.USERG_ID, A.DEPT_ID, A.JGD_NM, (select group_concat(db_name separator ',') as dbName from waa_user_db where user_id = A.USER_ID) DB_NAME, (select group_concat(wo.org_nm separator ',') as orgName from waa_org wo, waa_user_org wuo where wuo.user_id = A.USER_ID and wo.org_cd = wuo.org_cd) ORG_CD, A.USER_TELNO, A.USER_HTELNO, A.EMAIL_ADDR, A.EXCL_DWLD_AUTH_YN, A.ID_USE_EXP_DT, A.PWD_EXP_DT, A.OBJ_DESCN, A.OBJ_VERS, A.REG_TYP_CD, A.RQST_DTM, A.RQST_USER_ID, A.APRV_DCD, A.APRV_DTM, A.APRV_USER_ID, B.USERG_LNM, C.DEPT_NM FROM WAA_USER A INNER JOIN WAA_USERG B -- LEFT OUTER JOIN WAA_DEPT C ON A.USERG_ID = B.USERG_ID AND B.REG_TYP_CD IN ('C', 'U') AND B.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') LEFT OUTER JOIN WAA_DEPT C ON A.DEPT_ID = C.DEPT_ID AND C.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') AND C.REG_TYP_CD IN ('C', 'U') INNER JOIN (select wuo.user_id , wo.org_nm, wo.org_cd from WAA_ORG wo, WAA_USER_ORG wuo where wo.org_cd = wuo.org_cd) D ON A.USER_ID = D.USER_ID
A.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') AND A.REG_TYP_CD IN ('C', 'U')
AND A.USER_ID = #{userId,jdbcType=VARCHAR}
AND A.USER_NM LIKE CONCAT('%',#{userNm,jdbcType=VARCHAR},'%')
AND A.USERG_ID IN ('OBJ_00000034586', 'OBJ_00000034587')
AND C.DEPT_NM LIKE CONCAT('%',#{deptNm,jdbcType=VARCHAR},'%')
AND A.DEPT_ID = #{deptId,jdbcType=VARCHAR}
AND A.USER_TELNO = #{userTelno,jdbcType=VARCHAR}
AND A.USER_HTELNO = #{userHtelno,jdbcType=VARCHAR}
ORDER BY USER_NM

SELECT COUNT(*) FROM WAA_USER
WHERE USER_ID= #{userId}
  AND EXP_DTM =DATE_FORMAT('9999-12-31', '%Y-%m-%d')
