STND_CD = #{stndCd,jdbcType=VARCHAR}, SPCL_NT = #{spclNt,jdbcType=VARCHAR}, BSNSS_FLD = #{bsnssFld,jdbcType=VARCHAR}, PNM = #{pnm,jdbcType=VARCHAR}
where SDITM_ID = #{sditmId,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM (SELECT A.SDITM_ID ,A.SDITM_LNM ,A.SDITM_PNM ,A.LNM_CRI_DS ,A.DMN_ID ,A.DMNG_ID ,A.INFOTP_ID ,A.INFOTP_CHG_YN ,A.RQST_NO ,A.RQST_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,A.PNM_CRI_DS ,A.ERR_CHK ,A.CONFIRM_YN
,A.DATA_TYPE ,A.DATA_LEN ,A.DATA_SCAL , (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.FRS_RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS FRS_RQST_USER_NM , (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS RQST_USER_NM , (SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.APRV_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS APRV_USER_NM
,A.INFOTP_LNM, A.ALW_VAL, A.OWNR_ORG, A.ADMN_STND_CD ,A.SAVE_FRM, A.EXPRSN_FRM, A.UNIT, A.ORG_NM ,A.VRF_RMK , CASE WHEN A.ERR_CHK IS NOT null AND A.ERR_CHK != '' THEN '#FF0000' ELSE NULL END AS FontColor , A.DB_NM
, STND_CD , SPCL_NT , BSNSS_FLD , PNM FROM WAM_DB_SDITM A WHERE 1 = 1
AND replace(DB_NM, ' ','') IN (SELECT replace(DB_NAME, ' ','') AS DB_NAME FROM WAA_USER_DB WHERE USER_ID = #{userId,jdbcType=VARCHAR}) AND ORG_NM IN (SELECT ORG_NM FROM WAA_ORG WHERE ORG_CD IN (SELECT ORG_CD FROM WAA_USER_ORG WHERE USER_ID = #{userId,jdbcType=VARCHAR}))
AND A.VALID_YN ='Y' AND A.CONFIRM_YN ='N'
AND A.VALID_YN ='N' AND A.CONFIRM_YN ='N'
AND A.VALID_YN ='E' AND A.CONFIRM_YN ='N'
AND A.CONFIRM_YN ='Y' AND A.VALID_YN ='Y'
) FSQ -- 임시 Alias
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND (SDITM_LNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%' ) OR SDITM_PNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%' ) OR SDITM_LNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%' ) OR SDITM_PNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%' ) OR PNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR}, '%' ) OR PNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%' ) )
AND (DMN_LNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%' ) OR DMN_PNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%' ) OR DMN_LNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%' ) OR DMN_PNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%' ) OR PNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR}, '%' ) OR PNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%' ) )
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND INFOTP_CHG_YN = #{infotpChgYn,jdbcType=VARCHAR}
AND ENC_YN = #{encYn,jdbcType=VARCHAR}
AND OBJ_DESCN LIKE CONCAT('%' , #{objDescn,jdbcType=VARCHAR} , '%')
AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}
AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=TIMESTAMP}
AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
AND RQST_DTM = #{rqstDtm,jdbcType=TIMESTAMP}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND APRV_DTM = #{aprvDtm,jdbcType=TIMESTAMP}
AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') <= #{searchEndDe}
AND ORG_NM LIKE CONCAT('%',#{orgNm,jdbcType=VARCHAR},'%')
AND ( SDITM_LNM LIKE CONCAT('%' , #{stndNm,jdbcType=VARCHAR} , '%') OR SDITM_PNM LIKE CONCAT('%' , #{stndNm,jdbcType=VARCHAR} , '%') OR SDITM_LNM LIKE CONCAT('%' , UPPER(#{stndNm,jdbcType=VARCHAR}) , '%') OR SDITM_PNM LIKE CONCAT('%' , UPPER(#{stndNm,jdbcType=VARCHAR}) , '%') OR PNM LIKE CONCAT('%' , #{stndNm,jdbcType=VARCHAR}, '%' ) OR PNM LIKE CONCAT('%' , UPPER(#{stndNm,jdbcType=VARCHAR}) , '%' ) )
AND replace(DB_NM, ' ','') LIKE CONCAT('%',replace(#{dbNm,jdbcType=VARCHAR}, ' ',''),'%')
ORDER BY DB_NM,SDITM_LNM

SELECT * FROM (SELECT A.SDITM_ID ,A.SDITM_LNM ,A.SDITM_PNM ,A.LNM_CRI_DS ,A.DMN_ID ,A.DMNG_ID ,A.INFOTP_ID ,A.INFOTP_CHG_YN ,A.RQST_NO ,A.RQST_SNO ,A.OBJ_DESCN ,A.OBJ_VERS ,A.REG_TYP_CD ,A.FRS_RQST_DTM ,A.FRS_RQST_USER_ID ,A.RQST_DTM ,A.RQST_USER_ID ,A.APRV_DTM ,A.APRV_USER_ID ,A.PNM_CRI_DS ,A.PERS_INFO_YN ,B.DMN_LNM ,B.DMN_PNM ,D.DATA_TYPE ,D.DATA_LEN ,D.DATA_SCAL ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.FRS_RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS FRS_RQST_USER_NM ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS RQST_USER_NM ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.APRV_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS APRV_USER_NM ,G.DMNG_LNM ,G.DMNG_PNM ,G.UPP_DMNG_ID ,G.UPP_DMNG_LNM ,CONCAT( I.ITM_ENG_MEAN ,'_', DD.DMN_ENG_MEAN ) AS FULL_ENG_MEAN ,IFNULL(B.ENC_YN,'N') AS ENC_YN , CASE A.TRANS_YN WHEN 'Y' THEN '1' ELSE '0' END AS TRANS_YN FROM WAH_SDITM A INNER JOIN WAM_DB_DMN B ON A.DMN_ID = B.DMN_ID AND A.REG_TYP_CD IN ('C', 'U') AND A.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d') AND B.REG_TYP_CD IN ('C', 'U') LEFT JOIN WAA_INFO_TYPE D ON A.INFOTP_ID = D.INFOTP_ID AND D.EXP_DTM = DATE_FORMAT( '9999-12-31', '%Y-%m-%d') LEFT JOIN V_WAA_DMNG G ON A.DMNG_ID = G.DMNG_ID LEFT JOIN (SELECT A.OBJ_ID ,GROUP_CONCAT(B.ENG_MEAN ORDER BY A.WD_SNO SEPARATOR '_' ) AS ITM_ENG_MEAN FROM WAM_DB_STWD_CNFG A INNER JOIN WAM_DB_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID) I ON A.SDITM_ID = I.OBJ_ID LEFT JOIN (SELECT A.OBJ_ID ,GROUP_CONCAT(B.ENG_MEAN ORDER BY A.WD_SNO SEPARATOR '_' ) AS DMN_ENG_MEAN FROM WAM_DB_STWD_CNFG A INNER JOIN WAM_DB_STWD B ON A.STWD_ID = B.STWD_ID AND B.REG_TYP_CD IN ('C','U') GROUP BY A.OBJ_ID ) DD ON A.DMN_ID = DD.OBJ_ID
WHERE A.TRANS_YN = #{transYn,jdbcType=VARCHAR}
)
AND SDITM_ID = #{sditmId,jdbcType=VARCHAR}
AND (SDITM_LNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%' ) OR SDITM_PNM LIKE CONCAT('%' , #{sditmLnm,jdbcType=VARCHAR} , '%' ) OR SDITM_LNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%' ) OR SDITM_PNM LIKE CONCAT('%' , UPPER(#{sditmLnm,jdbcType=VARCHAR}) , '%' ))
AND (DMN_LNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%' ) OR DMN_PNM LIKE CONCAT('%' , #{dmnLnm,jdbcType=VARCHAR} , '%' ) OR DMN_LNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%' ) OR DMN_PNM LIKE CONCAT('%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%' ))
AND LNM_CRI_DS = #{lnmCriDs,jdbcType=VARCHAR}
AND DMN_ID = #{dmnId,jdbcType=VARCHAR}
AND DMNG_ID = #{dmngId,jdbcType=VARCHAR}
AND UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
AND INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
AND INFOTP_CHG_YN = #{infotpChgYn,jdbcType=VARCHAR}
AND ENC_YN = #{encYn,jdbcType=VARCHAR}
AND RQST_NO = #{rqstNo,jdbcType=VARCHAR}
AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
AND OBJ_DESCN LIKE CONCAT('%' , #{objDescn,jdbcType=VARCHAR} , '%')
AND OBJ_VERS = #{objVers,jdbcType=DECIMAL}
AND REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
AND FRS_RQST_DTM = #{frsRqstDtm,jdbcType=TIMESTAMP}
AND FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
AND RQST_DTM = #{rqstDtm,jdbcType=TIMESTAMP}
AND RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
AND APRV_DTM = #{aprvDtm,jdbcType=TIMESTAMP}
AND APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
AND DATA_TYPE = #{dataType,jdbcType=VARCHAR}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe}
AND DATE_FORMAT(APRV_DTM, '%Y-%m-%d') <= #{searchEndDe}
#{persInfoYn,jdbcType=VARCHAR},
ORDER BY TRANS_YN DESC, SDITM_LNM

