CONFIRM_DTM = SYSDATE, CONFIRM_YN = 'Y',
CONFIRM_YN = 'N',
VALID_YN = #{item.validYn,jdbcType=VARCHAR}
where SDITM_ID = #{item.sditmId,jdbcType=VARCHAR}

mapper.select:
