mapper.select:
SELECT * FROM WAM_DB_STWD WHERE  REG_TYP_CD IN ('C', 'U')
ORDER BY RQST_DTM DESC
limit 30;

mapper.delete:
delete WAM_DB_STWD
where STWD_ID in (select STWD_ID from WAH_STWD
                 where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
                   and EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d')
                 )

delete.foreach:
delete from WAM_DB_STWD
where STWD_ID = #{item.stwdId,jdbcType=VARCHAR}
 /* AND CONFIRM_YN = 'N' */

mapper.select:
SELECT COUNT(ORG_NM) 
FROM WAM_DB_STWD  
WHERE ORG_NM 	 = #{orgNm,jdbcType=VARCHAR}
AND DB_NM        = #{dbNm,jdbcType=VARCHAR}
AND STWD_LNM 	 = #{stwdLnm,jdbcType=VARCHAR}
AND CONFIRM_YN   = 'Y'

update.foreach:
update WAM_DB_STWD
   set
    ERR_CHK 		= #{item.errChk,jdbcType=VARCHAR},
    VALID_YN 		= #{item.validYn,jdbcType=VARCHAR}
where STWD_ID 		= #{item.stwdId,jdbcType=VARCHAR}

mapper.update:
update WAM_DB_STWD
set CONFIRM_DTM = sys_datetime,
    CONFIRM_YN =  'Y',
    ERR_CHK = ''
where VALID_YN = 'Y' AND ORG_NM = #{orgNm,jdbcType=VARCHAR}
  AND DB_NM = #{dbNm,jdbcType=VARCHAR}
