mapper.delete:
delete from WAQ_PRF_REL_TBL
where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
  and RQST_SNO = #{rqstSno,jdbcType=DECIMAL}

mapper.update:
	UPDATE WAQ_PRF_REL_TBL 
	     SET PRF_ID = #{prfId,jdbcType=VARCHAR}
	 WHERE RQST_NO = #{rqstNo,jdbcType=VARCHAR}
      AND RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
