mapper.select:
select A.* ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.FRS_RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS FRS_RQST_USER_NM ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.RQST_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS RQST_USER_NM ,(SELECT USER_NM FROM WAA_USER WHERE USER_ID = A.APRV_USER_ID and EXP_DTM = DATE_FORMAT('9999-12-31', '%Y-%m-%d')) AS APRV_USER_NM
from WAM_DMN A
where A.DMN_ID = #{dmnId,jdbcType=VARCHAR}

  	SELECT A.*




  	  FROM WAM_DMN A 













  	
       AND A.REG_TYP_CD IN ('C', 'U')
      
       AND (DMN_LNM LIKE CONCAT( '%' , #{dmnLnm,jdbcType=VARCHAR} , '%')
  	    
   	    OR DMN_LNM LIKE CONCAT( '%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%')
  	    )
      
      
        AND A.DMN_ID = #{dmnId,jdbcType=VARCHAR}
      
      
        AND A.INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
      
      
        AND A.UPP_DMN_ID = #{uppDmnId,jdbcType=VARCHAR}
      
      
        AND A.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_ID = #{lstEntyId,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_PNM = #{lstEntyPnm,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_LNM = #{lstEntyLnm,jdbcType=VARCHAR}
      
      
        AND A.CD_VAL_TYP_CD = #{cdValTypCd,jdbcType=VARCHAR}
      
      
        AND A.CD_VAL_IVW_CD = #{cdValIvwCd,jdbcType=VARCHAR}
      
      
        AND A.SDITM_AUTO_CRT_YN = #{sditmAutoCrtYn,jdbcType=VARCHAR}
      
      
        AND A.DATA_FRM = #{dataFrm,jdbcType=VARCHAR}
      
      
        AND A.CRG_USER_ID = #{crgUserId,jdbcType=VARCHAR}
      
      
        AND A.DMN_ORG_DS = #{dmnOrgDs,jdbcType=VARCHAR}
      
      
        AND A.DATA_TYPE = #{dataType,jdbcType=VARCHAR}
      
      
        AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
      
      
        AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
      
      
        AND A.OBJ_DESCN LIKE CONCAT( '%' , #{objDescn,jdbcType=VARCHAR} , '%')
      
      
        AND A.OBJ_VERS = #{objVers,jdbcType=DECIMAL}
      
      
        AND A.REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
      
      
        AND A.FRS_RQST_DTM = #{frsRqstDtm,jdbcType=DATE}
      
      
        AND A.FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
      
      
        AND A.RQST_DTM = #{rqstDtm,jdbcType=DATE}
      
      
        AND A.RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
      
      
        AND A.APRV_DTM = #{aprvDtm,jdbcType=DATE}
      
      
        AND A.APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
      






      
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe} 
	  
	  
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') <= #{searchEndDe} 
	  
	  	
        AND A.ENC_YN = #{encYn,jdbcType=VARCHAR}
      
         
        AND A.DMN_DSCD = #{dmnDscd,jdbcType=VARCHAR}
      
  	
  	ORDER BY DMN_LNM

mapper.delete:
delete WAM_DMN
where DMN_ID in (select DMN_ID from WAH_DMN
                 where RQST_NO = #{rqstNo,jdbcType=VARCHAR}
                   and EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d')
                 )

mapper.select:
  SELECT A.*





  	  FROM WAM_DMN A 








  	
       AND A.REG_TYP_CD IN ('C', 'U')
      
       AND (DMN_LNM LIKE CONCAT( '%' , #{dmnLnm,jdbcType=VARCHAR} , '%')

   	    OR DMN_LNM LIKE CONCAT( '%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%')
)
      
      
        AND A.DMN_ID = #{dmnId,jdbcType=VARCHAR}
      
      
        AND A.INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
      
      
        AND A.UPP_DMN_ID = #{uppDmnId,jdbcType=VARCHAR}
      
      
        AND A.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
      
      
        AND B.DATA_TYPE = #{dataType,jdbcType=VARCHAR}
      
      
        AND A.OBJ_DESCN LIKE CONCAT( '%' , #{objDescn,jdbcType=VARCHAR} , '%')
      
      
        AND C.CD_VAL LIKE CONCAT( '%' , #{cdVal,jdbcType=VARCHAR} , '%')
      
      
        AND C.CD_VAL_NM LIKE CONCAT( '%' , #{cdValNm,jdbcType=VARCHAR} , '%')
      
      
        AND G.DMNG_ID = #{dmngId,jdbcType=VARCHAR}
      
      
        AND G.UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
      
      
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe} 
	  
	  
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') <= #{searchEndDe} 
	  
	  
        AND a.ENC_YN = #{encYn,jdbcType=VARCHAR}
      
  	
  	ORDER BY DMN_LNM, C.CD_VAL

  	SELECT   A.DMN_ID                               
           , A.EXP_DTM                              
           , A.STR_DTM                              
           , A.DMN_LNM                              
           , A.DMN_PNM                              
           , A.LNM_CRI_DS                           
           , A.DMNG_ID                              
           , A.INFOTP_ID                            
           , A.UPP_DMN_ID                           
           , A.SUBJ_ID                              
           , A.LST_ENTY_ID                          
           , A.LST_ENTY_PNM                         
           , A.LST_ENTY_LNM                         
           , A.LST_ATTR_ID                          
           , A.LST_ATTR_PNM                         
           , A.LST_ATTR_LNM                         
           , A.CD_VAL_TYP_CD                        
           , A.CD_VAL_IVW_CD                        
           , A.SDITM_AUTO_CRT_YN                    
           , A.DATA_FRM                             
           , A.CRG_USER_ID                          
           , A.DMN_ORG_DS                           
           , A.RQST_NO                              
           , A.RQST_SNO                             
           , A.OBJ_DESCN                            
           , A.OBJ_VERS                             
           , A.REG_TYP_CD                           
           , A.FRS_RQST_DTM                         
           , A.FRS_RQST_USER_ID                     
           , A.RQST_DTM                             
           , A.RQST_USER_ID                         
           , A.APRV_DTM                             
           , A.APRV_USER_ID                         
           , A.FULL_PATH                            
           , A.PNM_CRI_DS                           
           , A.ENC_YN                               
           , A.DMN_DSCD                             
           , DECODE(A.TRANS_YN,'Y','1','0') TRANS_YN
  	       , B.DATA_TYPE
  	       , B.DATA_LEN
  	       , B.DATA_SCAL 
  	  FROM WAH_DMN A 
     INNER JOIN V_WAA_DMNG G
        ON A.DMNG_ID = G.DMNG_ID
  	 INNER JOIN WAA_INFO_TYPE B
  	    ON A.INFOTP_ID = B.INFOTP_ID
  	   AND B.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d')
  	
       AND A.REG_TYP_CD IN ('C', 'U')
       AND A.EXP_DTM = DATE_FORMAT( '99991231', '%Y%m%d')
      
       AND (DMN_LNM LIKE CONCAT( '%' , #{dmnLnm,jdbcType=VARCHAR} , '%')
  	    OR DMN_PNM LIKE CONCAT( '%' , #{dmnLnm,jdbcType=VARCHAR} , '%')
   	    OR DMN_LNM LIKE CONCAT( '%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%')
  	    OR DMN_PNM LIKE CONCAT( '%' , UPPER(#{dmnLnm,jdbcType=VARCHAR}) , '%'))
      
      
        AND A.DMN_ID = #{dmnId,jdbcType=VARCHAR}
      
      
        AND A.INFOTP_ID = #{infotpId,jdbcType=VARCHAR}
      
      
        AND A.UPP_DMN_ID = #{uppDmnId,jdbcType=VARCHAR}
      
      
        AND A.SUBJ_ID = #{subjId,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_ID = #{lstEntyId,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_PNM = #{lstEntyPnm,jdbcType=VARCHAR}
      
      
        AND A.LST_ENTY_LNM = #{lstEntyLnm,jdbcType=VARCHAR}
      
      
        AND A.CD_VAL_TYP_CD = #{cdValTypCd,jdbcType=VARCHAR}
      
      
        AND A.CD_VAL_IVW_CD = #{cdValIvwCd,jdbcType=VARCHAR}
      
      
        AND A.SDITM_AUTO_CRT_YN = #{sditmAutoCrtYn,jdbcType=VARCHAR}
      
      
        AND A.DATA_FRM = #{dataFrm,jdbcType=VARCHAR}
      
      
        AND A.CRG_USER_ID = #{crgUserId,jdbcType=VARCHAR}
      
      
        AND A.DMN_ORG_DS = #{dmnOrgDs,jdbcType=VARCHAR}
      
      
        AND B.DATA_TYPE = #{dataType,jdbcType=VARCHAR}
      
      
        AND A.RQST_NO = #{rqstNo,jdbcType=VARCHAR}
      
      
        AND A.RQST_SNO = #{rqstSno,jdbcType=DECIMAL}
      
      
        AND A.OBJ_DESCN LIKE CONCAT( '%' , #{objDescn,jdbcType=VARCHAR} , '%')
      
      
        AND A.OBJ_VERS = #{objVers,jdbcType=DECIMAL}
      
      
        AND A.REG_TYP_CD = #{regTypCd,jdbcType=VARCHAR}
      
      
        AND A.FRS_RQST_DTM = #{frsRqstDtm,jdbcType=DATE}
      
      
        AND A.FRS_RQST_USER_ID = #{frsRqstUserId,jdbcType=VARCHAR}
      
      
        AND A.RQST_DTM = #{rqstDtm,jdbcType=DATE}
      
      
        AND A.RQST_USER_ID = #{rqstUserId,jdbcType=VARCHAR}
      
      
        AND A.APRV_DTM = #{aprvDtm,jdbcType=DATE}
      
      
        AND A.APRV_USER_ID = #{aprvUserId,jdbcType=VARCHAR}
      
      
        AND G.DMNG_ID = #{dmngId,jdbcType=VARCHAR}
      
      
        AND G.UPP_DMNG_ID = #{uppDmngId,jdbcType=VARCHAR}
      
      
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') >= #{searchBgnDe} 
	  
	  
		 AND DATE_FORMAT(A.APRV_DTM, '%Y-%m-%d') <= #{searchEndDe} 
	  
	  
        AND A.ENC_YN = #{encYn,jdbcType=VARCHAR}
      
        
        AND A.DMN_DSCD = #{dmnDscd,jdbcType=VARCHAR}
      
      
        AND A.TRANS_YN = #{transYn,jdbcType=VARCHAR}
      
      
  	
  	ORDER BY TRANS_YN desc,DMN_LNM

